INSERT INTO Boisson VALUES (Coca Cola, null, 2, 'coca.jpg', null, null, null, 'Le Coca-Cola est une boisson gazeuse sucrée de type cola fabriquée par la Coca-Cola Company. Le nom Coca-Cola provient de deux ingrédients utilisés pour sa composition originelle : la feuille de coca et la noix de kola.');
INSERT INTO Boisson VALUES (Coca Light, null, 2.3, 'cocalight.jpg', null, null, null, 'Le Coca-Cola Light est une version du Coca-Cola sans sucres et sans calories, la marque est commercialisée aux États-Unis et dans d'autres pays sous la marque « Diet Coke ».');
INSERT INTO Boisson VALUES (Sprite, null, 2.3, 'sprite.jpg', null, null, null, 'Sprite est une boisson gazeuse à base d'extraits de citron et de lime créée par The Coca-Cola Company en 1961. Son goût est proche de la limonade. Le Sprite contient du dioxyde de carbone, de l'acide citrique, de l'eau gazéifiée, du citrate de sodium, des essences naturelles et du benzoate de sodium.');
INSERT INTO Boisson VALUES (Fanta, null, 2.3, 'fanta.jpg', null, null, null, 'Fanta est une boisson gazeuse sucrée au goût d'orange. C'est une marque commerciale de soda allemand produite et distribuée par The Coca-Cola Company.');
INSERT INTO Boisson VALUES (Orangina, null, 2.4, 'orangina.jpg', null, null, null, 'Orangina est une boisson gazeuse sucrée française faite à partir d'oranges et de citrons appartenant au groupe Orangina Schweppes, lui-même propriété du groupe Suntory.');
INSERT INTO Boisson VALUES (Ice Tea, null, 2.6, 'icetea.jpg', null, null, null, 'Ice Tea est une boisson gazeuse sucrée à base de thé glacé. Elle est commercialisée par PepsiCo. La marque Lipton appartient à Unilever.');
INSERT INTO Boisson VALUES (Ice Tea Pêche, null, 2.6, 'iceteapeche.jpg', null, null, null, 'Ice Tea Pêche est une boisson non gazeuse sucrée à base de thé glacé, saveur pêche. Elle est commercialisée par PepsiCo. La marque Lipton appartient à Unilever.');
INSERT INTO Boisson VALUES (Schweppes Tonic, null, 2.4, 'schweppestonic.jpg', null, null, null, 'Schweppes Tonic est une eau tonique, contenant des extraits de quinine et d'orange amère qui donnent au breuvage un goût amer. L'Indian Tonic s'inspire de la coutume des colons britanniques stationnés en Inde, qui buvaient régulièrement des breuvages contenant de la quinine pour se prémunir de la malaria.');
INSERT INTO Boisson VALUES (Schweppes Agrumes, null, 2.4, 'schweppesagrumes.jpg', null, null, null, 'Schweppes Agrumes est une eau gazeuse sucrée, sans quinie, aromatisée aux saveurs de quatre agrumes : orange, pamplemousse, citron vert, mandarine.');
INSERT INTO Boisson VALUES (Oasis, null, 2.4, 'oasis.jpg', null, null, null, 'Oasis est une boisson non gazeuse sucrée aromatisée aux fruits tropicaux, appartenant à Orangina Schweppes Group.');
INSERT INTO Boisson VALUES (Cécémel, null, 2.6, 'cecemel.jpg', null, null, null, 'Cécémel est une boisson chocolatée au lait produite par Campina aux Pays-Bas dans la ville d'Amersfoort.');
INSERT INTO Boisson VALUES (Sirop à l'eau, null, 1.5, 'sirop.jpg', null, null, null, 'Boisson non gazeuse composée d'eau et de sirop. Goûts disponibles : menthe, grenadine ou fraise.');
INSERT INTO Boisson VALUES (Red Bull, null, 3.9, 'redbull.jpg', null, null, null, 'Red Bull est une boisson énergisante contenant de l'eau minérale gazéifiée, de la caféine, de la taurine, de la Vitamine B ainsi que du saccharose et du glucose.');
INSERT INTO Boisson VALUES (Jus de fruit, null, 2.4, 'jusdefruit.jpg', null, null, null, 'Jus de fruits fraichement pressés. Saveur disponibles : orange, pomme ou ananas. ');
INSERT INTO Boisson VALUES (Eau Spa, null, 2.3, 'spa.jpg', null, null, null, 'L'eau de Spa est une eau minérale belge plate exploitée depuis plusieurs sources à Spa en province de Liège. Elle est produite par Spa Monopole S.A. qui fait partie du groupe Spadel.');
INSERT INTO Boisson VALUES (Eau Spa Pétillante, null, 2.3, 'spapetillante.jpg', null, null, null, 'L'eau de Spa est une eau minérale belge pétillante exploitée depuis plusieurs sources à Spa en province de Liège. Elle est produite par Spa Monopole S.A. qui fait partie du groupe Spadel');
INSERT INTO Boisson VALUES (Eau San Pellegrino, null, 4.2, 'sanpellegrino.jpg', null, null, null, 'Eau minérale originaire de la source de San Pellegrino en Italie, avec adjonction de gaz carbonique naturel.');
INSERT INTO Boisson VALUES (Café Petit, null, 1.5, 'cafe.jpg', null, null, null, 'Café de type Expresso très corsé avec un fort arôme, obtenu par percolation sous haute pression.');
INSERT INTO Boisson VALUES (Café Grand, null, 2.3, 'cafe.jpg', null, null, null, 'Double Expresso, café très corsé avec un fort arôme, obtenu par percolation sous haute pression.');
INSERT INTO Boisson VALUES (Café Crème Petit, null, 1.7, 'cafe.jpg', null, null, null, 'Café simple dans lequel on ajoute un nuage de crème fraîche.');
INSERT INTO Boisson VALUES (Café Crème Grand, null, 2.5, 'cafe.jpg', null, null, null, 'Café double dans lequel on ajoute un nuage de crème fraîche.');
INSERT INTO Boisson VALUES (Cappucino, null, 2.5, 'cafe.jpg', null, null, null, 'Café servi dans une grande tasse, à base d'un expresso et coiffé de lait préalablement chauffé à la vapeur jusqu'à le faire mousser. Il est saupoudré d'un peu de chocolat amer.');
INSERT INTO Boisson VALUES (Café Décaféiné, null, 2.4, 'cafe.jpg', null, null, null, 'Café sans caféine.');
INSERT INTO Boisson VALUES (Thé, null, 2.4, 'the.jpg', null, null, null, 'Thé préparé à base d'infusion. Goûts disponibles : menthe, thé vert ou jasmin.');
INSERT INTO Boisson VALUES (Chocolat Chaud, null, 2.7, 'chocolat.jpg', null, null, null, 'Boisson chaude constituée de chocolat fondu dans du lait demi-écrémé.');
INSERT INTO Boisson VALUES (Jupiler 25cl, null, 2.3, 'jupiler.jpg', null, null, null, 'Bière belge blonde de fermentation basse de type pils. Servie à la pression, dans un verre de 25 cl. Degrés d'alcool : 5,2°');
INSERT INTO Boisson VALUES (Jupiler 33cl, null, 2.8, 'jupiler.jpg', null, null, null, 'Bière belge blonde de fermentation basse de type pils. Servie à la pression, dans un verre de 33 cl. Degrés d'alcool : 5,2°');
INSERT INTO Boisson VALUES (Jupiler 50cl, null, 4.2, 'jupiler.jpg', null, null, null, 'Bière belge blonde de fermentation basse de type pils. Servie à la pression, dans un verre de 50 cl. Degrés d'alcool : 5,2°');
INSERT INTO Boisson VALUES (Leffe Blonde, null, 3.6, 'leffeblonde.jpg', null, null, null, 'Leffe Blonde est une authentique bière blonde d’Abbaye à la douce amertume qui se savoure à tout moment de la journée. Servie à la pression. Degrés d'alcool : 6,6°');
INSERT INTO Boisson VALUES (Leffe Brune, null, 3.6, 'leffebrune.jpg', null, null, null, 'Leffe Brune est une authentique bière d’Abbaye à la belle robe acajou et à la saveur pleine et légèrement sucrée qui fait de chaque gorgée un moment d’exception. Une couleur et une saveur uniques provenant de l’utilisation de malt torréfié. Servie à la pression. Degrés d'alcool : 6,5°');
INSERT INTO Boisson VALUES (Hoegarden Blanche, null, 2.5, 'hoegaardenblanche.jpg', null, null, null, 'La Hoegaarden Blanche est brassée avec de la coriandre et des zestes de citron pour plus de recherche de goût et de fraîcheur. Elle présente une intrigante robe d'un blond troublé qui s'explique par le fait que cette bière n'est pas filtrée. Servie à la pression, accompagnée d'une rondelle de citron. Degrés d'alcool : 4,9°');
INSERT INTO Boisson VALUES (Hoegarden Rosée, null, 3, 'hoegaardenrosee.jpg', null, null, null, 'Elle présente un arôme naturel de framboise prononcé et des touches subtiles d’épices et de coriandre. Comme la blanche originale, la Hoegaarden Rosée est non filtrée, ce qui lui donne son aspect trouble. Degrés d'alcool : 3°');
INSERT INTO Boisson VALUES (Carlsberg, null, 3.1, 'carlsberg.jpg', null, null, null, 'La bière Carlsberg se révèle sous une robe blonde claire mais aussi brillante. Elle se distingue par son éclat, sa fraicheur, sa fine carbonatation avec ses fines bulles. En bouche, la Carlsberg révèle de surprenants arômes. Degrés d'alcool : 5°');
INSERT INTO Boisson VALUES (Bush 12° Ambrée, null, 3.5, 'bush.jpg', null, null, null, 'La Bush Ambrée figure parmi les bières très fortes de Belgique. C'est une bière ambrée filtrée de haute fermentation. Elle libère dans le verre des arômes très fruités de malt et de caramel qui s'accompagnent d'effluves d'alcool. Degrés d'alcool : 12°');
INSERT INTO Boisson VALUES (Leffe Triple, null, 4.2, 'leffetriple.jpg', null, null, null, 'Leffe Triple est une authentique bière blonde d’Abbaye de caractère qui, grâce à la présence de levure, a bénéficié d’une refermentation en bouteille. Elle offre un arôme riche et nuancé. Degrés d'alcool : 8,5°');
INSERT INTO Boisson VALUES (Chimay Bleue, null, 4.2, 'chimaybleue.jpg', null, null, null, 'La Chimay Bleue est la plus forte et la plus complexe des bières trappistes de l'abbaye de Notre Dame de Scourmont Elle offre un bouquet d'épices et un nez fruité, très caractéristique de la Chimay. Degrés d'alcool : 9°');
INSERT INTO Boisson VALUES (Cuvée des Trolls, null, 3.1, 'trolls.jpg', null, null, null, 'La Cuvée des Trolls est aromatisée avec des écorces d’oranges séchées. Elles donnent à cette bière une note de fraîcheur et des arômes particulièrement agréables et fruités. Degrés d'alcool : 7°');
INSERT INTO Boisson VALUES (Desperados, null, 4.1, 'desperados.jpg', null, null, null, 'La bière Desperados est une blonde légère, aromatisée à la tequila. Cette bière est un produit spécial qui est à la limite entre cocktail alcoolisé et bière légère. Degrés d'alcool : 5,9°');
INSERT INTO Boisson VALUES (Gueuze Belle Vue, null, 3, 'bellevue.jpg', null, null, null, 'La Gueuze Bellevue est une bière de fermentation spontanée. Elle est composée d'un mélange de lambics jeunes et de lambic plus âgés. Son goût est a à la fois léger, doux, et amèr et sûre. Degrés d'alcool : 5,2°');
INSERT INTO Boisson VALUES (Tripel Karmeliet, null, 3.6, 'karmeliet.jpg', null, null, null, 'La bière Triple Karmeliet est l'un des grands classiques des bières belges. A la dégustation, cette bière belge présente une robe blonde dorée et une mousse crémeuse. Degrés d'alcool : 8°');
INSERT INTO Boisson VALUES (Lindemans Pêche, null, 4.1, 'lindemans.jpg', null, null, null, 'Cette Lindemans est une bière de type lambic, brassée à base de malts d'orge, de blé et avec ajout de jus de pêche avant une période de maturation en fût de chêne. Degrés d'alcool : 2,5°');
INSERT INTO Boisson VALUES (Rochefort 8°, null, 4.6, 'rochefort8.jpg', null, null, null, 'La Rochefort 8 est une bière trappiste originale et aromatisée. D'abord piquante, puis douce, la dégustation de cette bière se fait avec grand plaisir grâce à la rondeur apportée par le caramel et le chocolat. Degrés d'alcool : 9,2°');
INSERT INTO Boisson VALUES (Rochefort 10°, null, 5.6, 'rochefort10.jpg', null, null, null, 'La Rochefort 10° est une bière Trappiste brune. C'est la bière la plus alcoolisée des bières de l'abbaye de Rochefort et c'est aussi la plus riche. Degrés d'alcool : 11,3°');
INSERT INTO Boisson VALUES (Westmalle Triple, null, 4.3, 'westmalle.jpg', null, null, null, 'Ouvrez vous à ce qui est sans aucun doute l'une des bières trappistes les plus complexes qui existent avec cette Westmalle Triple. Degrés d'alcool : 9,5°');
INSERT INTO Boisson VALUES (Val Dieu Triple, null, 4.5, 'valdieu.jpg', null, null, null, 'La Val Dieu Triple est une bière belge de haute fermentation brassée à l'Abbaye du Val-Dieu à Aubel suivant une recette inspirée de celle utilisée par les moines brasseurs. Cette bière à la robe blonde limpide légèrement trouble cache de fines bulles et présente une belle mousse blanche onctueuse. Degrés d'alcool : 9°');
INSERT INTO Boisson VALUES (Chouffe, null, 4.1, 'chouffe.jpg', null, null, null, 'La Chouffe est une bière blonde non filtrée, refermentée trois fois en bouteille. Elle est agréablement fruitée, épicée à la coriandre et légèrement houblonnée. Degrés d'alcool : 8°');
INSERT INTO Boisson VALUES (Orval, null, 4.7, 'orval.jpg', null, null, null, 'L'Orval est une bière trappiste de dégustation, à la belle couleur orangée et présentant un beau nez aromatique. C'est une bière à l'arôme houblonné intense qui dévoile une légère acidité astringente et gagne à mûrir en cave pendant quelques années, debout et à l'abri de la lumière. Degrés d'alcool : 6,2°');
INSERT INTO Boisson VALUES (Duvel, null, 4, 'duvel.jpg', null, null, null, 'La bière Duvel est une bière belge fruitée dont la réputation n'est plus à faire. L'ensemble apparait extrêmement fruité quoi que faiblement sucré et l'harmonie des saveurs en fait oublier la présence de l'alcool dans cette bière blonde. Degrés d'alcool : 8,5°');
INSERT INTO Boisson VALUES (Gauloise Blonde, null, 3, 'gauloiseblonde.jpg', null, null, null, 'Vous serez vite conquis par sa jolie robe blonde dorée et sa mousse fine et crémeuse. En bouche, cette Gauloise blonde présente des saveurs fruitées accompagnées de pointes épicées. Degrés d'alcool : 6,3°');
INSERT INTO Boisson VALUES (Gauloise Ambrée, null, 3, 'gauloiseambree.jpg', null, null, null, 'La Gauloise Ambrée se caractérise par son arôme houblonné. Elle vous procurera un éventail de saveurs variées. Elle est très digeste et rafraîchissante. Degrés d'alcool : 5,5°');
INSERT INTO Boisson VALUES (Gauloise Brune, null, 3, 'gauloisebrune.jpg', null, null, null, 'Son malt torréfié lui donne une saveur caramélisée avec des notes de caramel, de chocolat et d'épices. Elle se caractérise par un équilibre savoureux. Degrés d'alcool : 8,1°');
INSERT INTO Boisson VALUES (Pastis Ricard, null, 4.5, 'pastis.jpg', null, null, null, 'Le Pastis Ricard est une boisson alcoolisée parfumée à l'anis et à la réglisse. Il se boit en apéritif, complété avec de l'eau. On ajoute généralement de cinq à sept volumes d'eau fraîche pour un volume de pastis.');
INSERT INTO Boisson VALUES (Martini, null, 3.9, 'martini.jpg', null, null, null, 'Martini est une marque de vermouth italien. Il se consomme avec de la glace, du citron ou en cocktail. Goûts disponibles : Rosso ou Bianco.');
INSERT INTO Boisson VALUES (Martini Royale, null, 5, 'martiniroyale.jpg', null, null, null, 'Martini Royale est un apéritif à base de vin, inspiré du fameux cocktail, mix inédit entre la douce amertume de Martini Bianco ou Rosso et la vive pétillance de MARTINI Prosecco.');
INSERT INTO Boisson VALUES (Campari, null, 4.5, 'campari.jpg', null, null, null, 'Campari est l'apéritif par excellence. Son goût doux-amer typique provient de plantes, de fruits et d'herbes, associés à du sucre et de l'eau distillée.');
INSERT INTO Boisson VALUES (Pisang, null, 4.5, 'pisang.jpg', null, null, null, 'Le Pisang est une liqueur de banane verte et fruits tropicaux, de couleur vert vif, dont la recette est basée sur une liqueur indonésienne. ');
INSERT INTO Boisson VALUES (Porto, null, 4.1, 'porto.jpg', null, null, null, 'Le porto est certainement le vin muté le plus célèbre. Comme les autres vins mutés, on l’élabore en ajoutant de l’alcool au moût durant la fermentation.');
INSERT INTO Boisson VALUES (Pineau des Charentes, null, 3.9, 'pineau.jpg', null, null, null, 'Le pineau des Charentes est une mistelle, vin de liqueur de qualité, obtenue par mélange de moût et d'eau-de-vie de cognac.');
INSERT INTO Boisson VALUES (Picon, null, 4.4, 'picon.jpg', null, null, null, 'Le Picon est un amer, de couleur caramel qui accompagne traditionnellement la bière.');
INSERT INTO Boisson VALUES (Kir Sauvignon, null, 4.3, 'kir.jpg', null, null, null, 'Le kir ou blanc-cassis est un cocktail traditionnel de la cuisine bourguignonne, à base de crème de cassis et de bourgogne aligoté.');
INSERT INTO Boisson VALUES (Kir Royal, null, 9.7, 'kirroyal.jpg', null, null, null, 'Le kir royal est une variante du kir sauvignon, à base de crème de cassis et de champagne.');
INSERT INTO Boisson VALUES (Mojito, null, 7.5, 'mojito.jpg', null, null, null, 'Le Mojito est un cocktail à base de rhum, de citron vert et de feuilles de menthe fraîche.');
INSERT INTO Boisson VALUES (Cuba Libre, null, 7.5, 'cubalibre.jpg', null, null, null, 'Le Cuba libre est un cocktail à base de rhum, de citron vert et de cola, appelé aussi rhum-Coca.');
INSERT INTO Boisson VALUES (Bloody Mary, null, 7.5, 'bloodymary.jpg', null, null, null, 'Le Bloody Mary est un cocktail plus ou moins fortement pimenté et épicé selon les goûts, à base de vodka, de jus de tomate, de jus de citron et d'épices telles que piment, sauce Tabasco, sauce Worcestershire, poivre, sel au céleri…');
INSERT INTO Boisson VALUES (Margarita, null, 7.5, 'margarita.jpg', null, null, null, 'La Margarita est un cocktail à base de tequila, cointreau et jus de citron.');
INSERT INTO Boisson VALUES (Get 27, null, 4.7, 'get27.jpg', null, null, null, 'Get 27 est une liqueur à la menthe à base d'huiles essentielles de menthe, d'alcool, d'eau et de sucre. Elle se déguste avec glaçons, ou diluée à l'eau.');
INSERT INTO Boisson VALUES (Grappa, null, 5.7, 'grappa.jpg', null, null, null, 'La grappa est une eau-de-vie de marc de raisin produite en Italie.');
INSERT INTO Boisson VALUES (Vodka, null, 4.7, 'vodka.jpg', null, null, null, 'La vodka est une boisson alcoolisée incolore de 40 degrés. Il s'agit généralement d'une eau-de-vie de pomme de terre ou de céréales.');
INSERT INTO Boisson VALUES (Gin, null, 4.7, 'gin.jpg', null, null, null, 'Le gin est une boisson alcoolisée obtenue en aromatisant de l'alcool rectifié d'origine agricole avec des baies de genévrier.');
INSERT INTO Boisson VALUES (Bacardi, null, 4.7, 'bacardi.jpg', null, null, null, 'Le rhum blanc Bacardi est une eau-de-vie produite à partir de la canne à sucre.');
INSERT INTO Boisson VALUES (Bailey's, null, 4.7, 'baileys.jpg', null, null, null, 'Baileys Irish Cream est une liqueur à base de whiskey irlandais et de crème.');
INSERT INTO Boisson VALUES (Tequila, null, 5.8, 'tequila.jpg', null, null, null, 'La tequila est une boisson alcoolisée produite au Mexique à partir d'une plante nommée Agave tequilana. Servie avec un quart de citron et du sel.');
INSERT INTO Boisson VALUES (William Lawson, null, 4.7, 'williamlawson.jpg', null, null, null, 'William Lawson est ce qu'on appelle un blend, c'est-à-dire un mélange de plusieurs sortes de whisky.');
INSERT INTO Boisson VALUES (Jack Daniel's, null, 5.5, 'jackdaniels.jpg', null, null, null, 'Le Jack Daniel’s est un Tennessee Whisky, il est fabriqué majoritairement à base de maïs et répond à tous les critères d’appellation du bourbon. On peut très bien être un bourbon et avoir quelque chose en nous de Tennessee.');
INSERT INTO Boisson VALUES (Amaretto, null, 4.7, 'amaretto.jpg', null, null, null, 'L'amaretto est un digestif avec un léger parfum d'amandes amères, originaire d'Italie.');
INSERT INTO Boisson VALUES (Cognac, null, 4.7, 'cognac.jpg', null, null, null, 'Le cognac est une eau-de-vie fine à base de raisin, et vieillie pendant au moins deux ans et demi en fûts de chêne. ');
INSERT INTO Boisson VALUES (Calvados, null, 6.6, 'calvados.jpg', null, null, null, 'Le calvados est une eau-de-vie d'origine normande obtenue par distillation de cidre ou de poiré.');
INSERT INTO Boisson VALUES (Cointreau, null, 6.6, 'cointreau.jpg', null, null, null, 'Le cointreau est une liqueur à base d'écorces d'oranges douces et amères proche du triple sec.');
INSERT INTO Boisson VALUES (Limoncello, null, 5.3, 'limoncello.jpg', null, null, null, 'Le limoncello est une liqueur de citron obtenu par macération d'écorces de citron dans l'alcool ou la grappa.');
