INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Coca Cola", 4, 2, "Le Coca-Cola est une boisson gazeuse sucrée de type cola fabriquée par la Coca-Cola Company. Le nom « Coca-Cola » provient de deux ingrédients utilisés pour sa composition originelle : la feuille de coca et la noix de kola.", "coca.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Coca Light", 4, 2.3, "Le Coca-Cola Light est une version du Coca-Cola sans sucres et sans calories, la marque est commercialisée aux États-Unis et dans d'autres pays sous la marque « Diet Coke ».", "cocalight.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Sprite", 4, 2.3, "Sprite est une boisson gazeuse à base d'extraits de citron et de lime créée par The Coca-Cola Company en 1961. Son goût est proche de la limonade. Le Sprite contient du dioxyde de carbone, de l'acide citrique, de l'eau gazéifiée, du citrate de sodium, des essences naturelles et du benzoate de sodium.", "sprite.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Fanta", 4, 2.3, "Fanta est une boisson gazeuse sucrée au goût d'orange. C'est une marque commerciale de soda allemand produite et distribuée par The Coca-Cola Company.", "fanta.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Orangina", 4, 2.4, "Orangina est une boisson gazeuse sucrée française faite à partir d'oranges et de citrons appartenant au groupe Orangina Schweppes, lui-même propriété du groupe Suntory.", "orangina.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Ice Tea", 4, 2.6, "Ice Tea est une boisson gazeuse sucrée à base de thé glacé. Elle est commercialisée par PepsiCo. La marque Lipton appartient à Unilever.", "icetea.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Ice Tea Pêche", 4, 2.6, "Ice Tea Pêche est une boisson non gazeuse sucrée à base de thé glacé, saveur pêche. Elle est commercialisée par PepsiCo. La marque Lipton appartient à Unilever.", "iceteapeche.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Schweppes Tonic", 4, 2.4, "Schweppes Tonic est une eau tonique, contenant des extraits de quinine et d'orange amère qui donnent au breuvage un goût amer. L'Indian Tonic s'inspire de la coutume des colons britanniques stationnés en Inde, qui buvaient régulièrement des breuvages contenant de la quinine pour se prémunir de la malaria.", "schweppestonic.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Schweppes Agrumes", 4, 2.4, "Schweppes Agrumes est une eau gazeuse sucrée, sans quinie, aromatisée aux saveurs de quatre agrumes : orange, pamplemousse, citron vert, mandarine.", "schweppesagrumes.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Oasis", 4, 2.4, "Oasis est une boisson non gazeuse sucrée aromatisée aux fruits tropicaux, appartenant à Orangina Schweppes Group.", "oasis.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Cécémel", 4, 2.6, "Cécémel est une boisson chocolatée au lait produite par Campina aux Pays-Bas dans la ville d'Amersfoort.", "cecemel.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Sirop à l'eau", 4, 1.5, "Boisson non gazeuse composée d'eau et de sirop. Goûts disponibles : menthe, grenadine ou fraise.", "sirop.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Red Bull", 4, 3.9, "Red Bull est une boisson énergisante contenant de l'eau minérale gazéifiée, de la caféine, de la taurine, de la Vitamine B ainsi que du saccharose et du glucose.", "redbull.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Jus de fruit", 4, 2.4, "Jus de fruits fraichement pressés. Saveur disponibles : orange, pomme ou ananas. ", "jusdefruit.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Eau Spa", 4, 2.3, "L'eau de Spa est une eau minérale belge plate exploitée depuis plusieurs sources à Spa en province de Liège. Elle est produite par Spa Monopole S.A. qui fait partie du groupe Spadel.", "spa.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Eau Spa Pétillante", 4, 2.3, "L'eau de Spa est une eau minérale belge pétillante exploitée depuis plusieurs sources à Spa en province de Liège. Elle est produite par Spa Monopole S.A. qui fait partie du groupe Spadel", "spapetillante.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Eau San Pellegrino", 4, 4.2, "Eau minérale originaire de la source de San Pellegrino en Italie, avec adjonction de gaz carbonique naturel.", "sanpellegrino.jpg", "Softs", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Café Petit", 4, 1.5, "Café de type Expresso très corsé avec un fort arôme, obtenu par percolation sous haute pression.", "cafe.jpg", "Chaudes", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Café Grand", 4, 2.3, "Double Expresso, café très corsé avec un fort arôme, obtenu par percolation sous haute pression.", "cafe.jpg", "Chaudes", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Café Crème Petit", 4, 1.7, "Café simple dans lequel on ajoute un nuage de crème fraîche.", "cafe.jpg", "Chaudes", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Café Crème Grand", 4, 2.5, "Café double dans lequel on ajoute un nuage de crème fraîche.", "cafe.jpg", "Chaudes", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Cappucino", 4, 2.5, "Café servi dans une grande tasse, à base d'un expresso et coiffé de lait préalablement chauffé à la vapeur jusqu'à le faire mousser. Il est saupoudré d'un peu de chocolat amer.", "cafe.jpg", "Chaudes", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Café Décaféiné", 4, 2.4, "Café sans caféine.", "cafe.jpg", "Chaudes", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Thé", 4, 2.4, "Thé préparé à base d'infusion. Goûts disponibles : menthe, thé vert ou jasmin.", "the.jpg", "Chaudes", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Chocolat Chaud", 4, 2.7, "Boisson chaude constituée de chocolat fondu dans du lait demi-écrémé.", "chocolat.jpg", "Chaudes", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Jupiler 25cl", 4, 2.3, "Bière belge blonde de fermentation basse de type pils. Servie à la pression, dans un verre de 25 cl. Degrés d'alcool : 5,2°", "jupiler.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Jupiler 33cl", 4, 2.8, "Bière belge blonde de fermentation basse de type pils. Servie à la pression, dans un verre de 33 cl. Degrés d'alcool : 5,2°", "jupiler.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Jupiler 50cl", 4, 4.2, "Bière belge blonde de fermentation basse de type pils. Servie à la pression, dans un verre de 50 cl. Degrés d'alcool : 5,2°", "jupiler.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Leffe Blonde", 4, 3.6, "Leffe Blonde est une authentique bière blonde d’Abbaye à la douce amertume qui se savoure à tout moment de la journée. Servie à la pression. Degrés d'alcool : 6,6°", "leffeblonde.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Leffe Brune", 4, 3.6, "Leffe Brune est une authentique bière d’Abbaye à la belle robe acajou et à la saveur pleine et légèrement sucrée qui fait de chaque gorgée un moment d’exception. Une couleur et une saveur uniques provenant de l’utilisation de malt torréfié. Servie à la pression. Degrés d'alcool : 6,5°", "leffebrune.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Hoegarden Blanche", 4, 2.5, "La Hoegaarden Blanche est brassée avec de la coriandre et des zestes de citron pour plus de recherche de goût et de fraîcheur. Elle présente une intrigante robe d'un blond troublé qui s'explique par le fait que cette bière n'est pas filtrée. Servie à la pression, accompagnée d'une rondelle de citron. Degrés d'alcool : 4,9°", "hoegaardenblanche.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Hoegarden Rosée", 4, 3, "Elle présente un arôme naturel de framboise prononcé et des touches subtiles d’épices et de coriandre. Comme la blanche originale, la Hoegaarden Rosée est non filtrée, ce qui lui donne son aspect trouble. Degrés d'alcool : 3°", "hoegaardenrosee.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Carlsberg", 4, 3.1, "La bière Carlsberg se révèle sous une robe blonde claire mais aussi brillante. Elle se distingue par son éclat, sa fraicheur, sa fine carbonatation avec ses fines bulles. En bouche, la Carlsberg révèle de surprenants arômes. Degrés d'alcool : 5°", "carlsberg.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Bush 12° Ambrée", 4, 3.5, "La Bush Ambrée figure parmi les bières très fortes de Belgique. C'est une bière ambrée filtrée de haute fermentation. Elle libère dans le verre des arômes très fruités de malt et de caramel qui s'accompagnent d'effluves d'alcool. Degrés d'alcool : 12°", "bush.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Leffe Triple", 4, 4.2, "Leffe Triple est une authentique bière blonde d’Abbaye de caractère qui, grâce à la présence de levure, a bénéficié d’une refermentation en bouteille. Elle offre un arôme riche et nuancé. Degrés d'alcool : 8,5°", "leffetriple.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Chimay Bleue", 4, 4.2, "La Chimay Bleue est la plus forte et la plus complexe des bières trappistes de l'abbaye de Notre Dame de Scourmont Elle offre un bouquet d'épices et un nez fruité, très caractéristique de la Chimay. Degrés d'alcool : 9°", "chimaybleue.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Cuvée des Trolls", 4, 3.1, "La Cuvée des Trolls est aromatisée avec des écorces d’oranges séchées. Elles donnent à cette bière une note de fraîcheur et des arômes particulièrement agréables et fruités. Degrés d'alcool : 7°", "trolls.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Desperados", 4, 4.1, "La bière Desperados est une blonde légère, aromatisée à la tequila. Cette bière est un produit spécial qui est à la limite entre cocktail alcoolisé et bière légère. Degrés d'alcool : 5,9°", "desperados.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Gueuze Belle Vue", 4, 3, "La Gueuze Bellevue est une bière de fermentation spontanée. Elle est composée d'un mélange de lambics jeunes et de lambic plus âgés. Son goût est a à la fois léger, doux, et amèr et sûre. Degrés d'alcool : 5,2°", "bellevue.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Tripel Karmeliet", 4, 3.6, "La bière Triple Karmeliet est l'un des grands classiques des bières belges. A la dégustation, cette bière belge présente une robe blonde dorée et une mousse crémeuse. Degrés d'alcool : 8°", "karmeliet.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Lindemans Pêche", 4, 4.1, "Cette Lindemans est une bière de type lambic, brassée à base de malts d'orge, de blé et avec ajout de jus de pêche avant une période de maturation en fût de chêne. Degrés d'alcool : 2,5°", "lindemans.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Rochefort 8°", 4, 4.6, "La Rochefort 8 est une bière trappiste originale et aromatisée. D'abord piquante, puis douce, la dégustation de cette bière se fait avec grand plaisir grâce à la rondeur apportée par le caramel et le chocolat. Degrés d'alcool : 9,2°", "rochefort8.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Rochefort 10°", 4, 5.6, "La Rochefort 10° est une bière Trappiste brune. C'est la bière la plus alcoolisée des bières de l'abbaye de Rochefort et c'est aussi la plus riche. Degrés d'alcool : 11,3°", "rochefort10.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Westmalle Triple", 4, 4.3, "Ouvrez vous à ce qui est sans aucun doute l'une des bières trappistes les plus complexes qui existent avec cette Westmalle Triple. Degrés d'alcool : 9,5°", "westmalle.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Val Dieu Triple", 4, 4.5, "La Val Dieu Triple est une bière belge de haute fermentation brassée à l'Abbaye du Val-Dieu à Aubel suivant une recette inspirée de celle utilisée par les moines brasseurs. Cette bière à la robe blonde limpide légèrement trouble cache de fines bulles et présente une belle mousse blanche onctueuse. Degrés d'alcool : 9°", "valdieu.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Chouffe", 4, 4.1, "La Chouffe est une bière blonde non filtrée, refermentée trois fois en bouteille. Elle est agréablement fruitée, épicée à la coriandre et légèrement houblonnée. Degrés d'alcool : 8°", "chouffe.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Orval", 4, 4.7, "L'Orval est une bière trappiste de dégustation, à la belle couleur orangée et présentant un beau nez aromatique. C'est une bière à l'arôme houblonné intense qui dévoile une légère acidité astringente et gagne à mûrir en cave pendant quelques années, debout et à l'abri de la lumière. Degrés d'alcool : 6,2°", "orval.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Duvel", 4, 4, "La bière Duvel est une bière belge fruitée dont la réputation n'est plus à faire. L'ensemble apparait extrêmement fruité quoi que faiblement sucré et l'harmonie des saveurs en fait oublier la présence de l'alcool dans cette bière blonde. Degrés d'alcool : 8,5°", "duvel.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Gauloise Blonde", 4, 3, "Vous serez vite conquis par sa jolie robe blonde dorée et sa mousse fine et crémeuse. En bouche, cette Gauloise blonde présente des saveurs fruitées accompagnées de pointes épicées. Degrés d'alcool : 6,3°", "gauloiseblonde.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Gauloise Ambrée", 4, 3, "La Gauloise Ambrée se caractérise par son arôme houblonné. Elle vous procurera un éventail de saveurs variées. Elle est très digeste et rafraîchissante. Degrés d'alcool : 5,5°", "gauloiseambree.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Gauloise Brune", 4, 3, "Son malt torréfié lui donne une saveur caramélisée avec des notes de caramel, de chocolat et d'épices. Elle se caractérise par un équilibre savoureux. Degrés d'alcool : 8,1°", "gauloisebrune.jpg", "Bières", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Pastis Ricard", 4, 4.5, "Le Pastis Ricard est une boisson alcoolisée parfumée à l'anis et à la réglisse. Il se boit en apéritif, complété avec de l'eau. On ajoute généralement de cinq à sept volumes d'eau fraîche pour un volume de pastis.", "pastis.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Martini", 4, 3.9, "Martini est une marque de vermouth italien. Il se consomme avec de la glace, du citron ou en cocktail. Goûts disponibles : Rosso ou Bianco.", "martini.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Martini Royale", 4, 5, "Martini Royale est un apéritif à base de vin, inspiré du fameux cocktail, mix inédit entre la douce amertume de Martini Bianco ou Rosso et la vive pétillance de MARTINI Prosecco.", "martiniroyale.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Campari", 4, 4.5, "Campari est l'apéritif par excellence. Son goût doux-amer typique provient de plantes, de fruits et d'herbes, associés à du sucre et de l'eau distillée.", "campari.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Pisang", 4, 4.5, "Le Pisang est une liqueur de banane verte et fruits tropicaux, de couleur vert vif, dont la recette est basée sur une liqueur indonésienne. ", "pisang.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Porto", 4, 4.1, "Le porto est certainement le vin muté le plus célèbre. Comme les autres vins mutés, on l’élabore en ajoutant de l’alcool au moût durant la fermentation.", "porto.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Pineau des Charentes", 4, 3.9, "Le pineau des Charentes est une mistelle, vin de liqueur de qualité, obtenue par mélange de moût et d'eau-de-vie de cognac.", "pineau.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Picon", 4, 4.4, "Le Picon est un amer, de couleur caramel qui accompagne traditionnellement la bière.", "picon.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Kir Sauvignon", 4, 4.3, "Le kir ou blanc-cassis est un cocktail traditionnel de la cuisine bourguignonne, à base de crème de cassis et de bourgogne aligoté.", "kir.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Kir Royal", 4, 9.7, "Le kir royal est une variante du kir sauvignon, à base de crème de cassis et de champagne.", "kirroyal.jpg", "Spiritueux", 4, 4, 4);
INSERT INTO [Boisson] ([Nom], [PrixAchat], [PrixVente], [Description], [Photo], [Type], [Stock], [Seuil], [Max]) VALUES ("Mojito", 4, 7.5, "Le Mojito est un cocktail à base de rhum, de citron vert et de feuilles de menthe fraîche.", "mojito.jpg", "Spiritueux", 4, 4, 4);
