INSERT INTO boissons VALUES (Coca Cola, 0.3, 2, 'coca.jpg', 56, 72, 24, '"Coca-Cola is a sweet soft drink cola manufactured by the Coca-Cola Company. The name ""Coca-Cola"" comes from two ingredients used for its original composition: the coca leaf and kola nut."');
INSERT INTO boissons VALUES (Coca Light, 0.35, 2.3, 'cocalight.jpg', 64, 72, 24, '"The Coca-Cola Light is a version of Coca-Cola without sugar and no calories, the brand is marketed in the United States and in other countries under the ""Diet Coke""."');
INSERT INTO boissons VALUES (Sprite, 0.35, 2.3, 'sprite.jpg', 35, 48, 12, 'Sprite is a soft drink made with lemon and lime extracts created by The Coca-Cola Company in 1961. Its taste is close to the lemonade. Sprite contains carbon dioxide, citric acid, soda water, sodium citrate, natural essences and sodium benzoate.');
INSERT INTO boissons VALUES (Fanta, 0.35, 2.3, 'fanta.jpg', 43, 48, 12, 'Fanta is a soft drink with orange flavor. It is a trademark of German soda produced and distributed by The Coca-Cola Company.');
INSERT INTO boissons VALUES (Orangina, 0.4, 2.4, 'orangina.jpg', 32, 48, 12, 'Orangina is a French sweet soft drink made from oranges and lemons in the group Orangina Schweppes, itself owned by the Suntory group.');
INSERT INTO boissons VALUES (Ice Tea, 0.45, 2.6, 'icetea.jpg', 27, 48, 12, 'Ice Tea is a soft drink made from sweet iced tea. It is marketed by PepsiCo. The brand is owned by Unilever Lipton.');
INSERT INTO boissons VALUES (Ice Tea Peach, 0.45, 2.6, 'iceteapeche.jpg', 35, 48, 12, 'Ice Tea Peach is a non-sparkling drink made from sweet iced tea, peach flavor. It is marketed by PepsiCo. The brand is owned by Unilever Lipton.');
INSERT INTO boissons VALUES (Schweppes Tonic, 0.4, 2.4, 'schweppestonic.jpg', 41, 48, 12, 'Schweppes Tonic is a tonic water containing quinine and bitter orange extracts that give the beverage a bitter taste. The Indian Tonic is based on the custom of British settlers stationed in India who regularly drank beverages containing quinine to guard against malaria.');
INSERT INTO boissons VALUES (Schweppes Agrum, 0.4, 2.4, 'schweppesagrumes.jpg', 39, 48, 12, 'Schweppes Citrus is a sweet sparkling water without quinie, flavored with four flavors of citrus fruits: orange, grapefruit, lime, tangerine.');
INSERT INTO boissons VALUES (Oasis, 0.4, 2.4, 'oasis.jpg', 33, 48, 12, 'Oasis is a non-sparkling drink flavored with sweet tropical fruits, owned by Orangina Schweppes Group.');
INSERT INTO boissons VALUES (Chocomel, 0.45, 2.6, 'cecemel.jpg', 17, 24, 6, 'Chocomel is a chocolate drink milk produced by Campina in the Netherlands in the city of Amersfoort.');
INSERT INTO boissons VALUES (Syrup with water, 0.2, 1.5, 'sirop.jpg', 42, 60, 20, 'Non-sparkling drink composed of water and syrup. Flavours available: mint, grenadine or strawberry.');
INSERT INTO boissons VALUES (Red Bull, 0.6, 3.9, 'redbull.jpg', 51, 72, 24, 'Red Bull is an energy drink containing carbonated mineral water, caffeine, taurine, Vitamin B as well as sucrose and glucose.');
INSERT INTO boissons VALUES (Fruit Juice, 0.4, 2.4, 'jusdefruit.jpg', 25, 50, 12, 'Freshly squeezed fruit juice. Flavor available: orange, apple or pineapple.');
INSERT INTO boissons VALUES (Spa Water, 0.35, 2.3, 'spa.jpg', 43, 72, 24, 'Spa water is a Belgian mineral water exploited from multiple sources in Spa in the province of Liège. It is produced by Spa Monopole SA, part of Spadel group.');
INSERT INTO boissons VALUES (Spa Sparkling Water, 0.35, 2.3, 'spapetillante.jpg', 57, 72, 24, 'Sparkling Spa water is a Belgian mineral water exploited from multiple sources in Spa in the province of Liège. It is produced by Spa Monopole SA, part of Spadel group.');
INSERT INTO boissons VALUES (San Pellegrino 50cl, 0.7, 4.2, 'sanpellegrino.jpg', 41, 48, 12, 'Mineral water originating from the source of San Pellegrino in Italy, with the addition of natural carbon dioxide.');
INSERT INTO boissons VALUES (Coffee Small, 0.2, 1.5, 'cafe.jpg', 22, 60, 20, 'Espresso-type coffee very strong with a strong aroma, obtained by percolation under high pressure.');
INSERT INTO boissons VALUES (Coffee Large, 0.35, 2.3, 'cafe.jpg', 25, 60, 20, 'Double Espresso, very spicy coffee with a strong aroma, obtained by percolation under high pressure.');
INSERT INTO boissons VALUES (White Coffee Small, 0.25, 1.7, 'cafe.jpg', 34, 60, 20, 'Coffee where we add a cloud of cream.');
INSERT INTO boissons VALUES (White Coffee Large, 0.42, 2.5, 'cafe.jpg', 32, 60, 20, 'Double coffee where we add a cloud of cream.');
INSERT INTO boissons VALUES (Cappucino, 0.42, 2.5, 'cafe.jpg', 41, 60, 20, 'Coffee served in a large cup, made from espresso and topped with preheated milk. It is sprinkled with a little bitter chocolate.');
INSERT INTO boissons VALUES (Decaffeinated Coffee, 0.4, 2.4, 'cafe.jpg', 43, 50, 12, 'Coffee without caffeine.');
INSERT INTO boissons VALUES (Tea, 0.4, 2.4, 'the.jpg', 35, 50, 12, 'Tea infusion. Flavours available: mint, green tea or jasmine.');
INSERT INTO boissons VALUES (Hot Chocolate, 0.47, 2.7, 'chocolat.jpg', 26, 50, 12, 'Hot drink made with melted chocolate in semi-skimmed milk.');
INSERT INTO boissons VALUES (Jupiler 25cl, 0.35, 2.3, 'jupiler.jpg', 97, 120, 48, 'Belgian blond beer of low fermentation, lager type. Tap served in a 25 cl glass. Degrees of alcohol: 5.2 °');
INSERT INTO boissons VALUES (Jupiler 33cl, 0.5, 2.8, 'jupiler.jpg', 49, 96, 32, 'Belgian blond beer of low fermentation, lager type. Tap served in a 33 cl glass. Degrees of alcohol: 5.2 °');
INSERT INTO boissons VALUES (Jupiler 50cl, 0.7, 4.2, 'jupiler.jpg', 58, 72, 24, 'Belgian blond beer of low fermentation, lager type. Tap served in a 50 cl glass. Degrees of alcohol: 5.2 °');
INSERT INTO boissons VALUES (Leffe Blond, 0.55, 3.6, 'leffeblonde.jpg', 36, 48, 12, 'Leffe Blond is an authentic abbey blond beer with a sweet bitterness that is savored at any time of the day. Served on tap. Degrees of alcohol: 6.6 °');
INSERT INTO boissons VALUES (Leffe Brown, 0.55, 3.6, 'leffebrune.jpg', 21, 24, 6, 'Leffe Brown is an authentic abbey beer to beautiful mahogany color and full, slightly sweet flavor that makes every sip an exceptional moment. A color and a unique flavor resulting from the use of roasted malt. Served on tap. Degrees of alcohol: 6.5 °');
INSERT INTO boissons VALUES (Hoegarden White, 0.42, 2.5, 'hoegaardenblanche.jpg', 25, 48, 12, 'Hoegaarden White is brewed with coriander and lemon zest for research in taste and freshness. It presents an intriguing dress of a troubled blonde is explained by the fact that the beer is not filtered. Served on tap, along with a slice of lemon. Degrees of alcohol: 4.9 °');
INSERT INTO boissons VALUES (Hoegarden Pink, 0.51, 3, 'hoegaardenrosee.jpg', 19, 48, 12, 'It has a natural flavor raspberry pronounced and subtle touches of spices and coriander. Like the original white, Hoegaarden Pink is unfiltered, giving it its cloudy appearance. Degrees of alcohol: 3');
INSERT INTO boissons VALUES (Carlsberg, 0.52, 3.1, 'carlsberg.jpg', 39, 72, 24, 'Carlsberg beer is revealed in a clear blond dress but also brilliant. It is distinguished by its brightness, freshness, fine carbonation with its fine bubbles. In the mouth, the Carlsberg reveals surprising flavors. Degrees of alcohol: 5 °');
INSERT INTO boissons VALUES (Bush 12° Amber, 0.54, 3.5, 'bush.jpg', 13, 24, 6, 'The Bush Amber is one of the very strong beers from Belgium. It is an amber beer filtered and from high fermentation. It releases in glass of fruity aromas of caramel malt and which are accompanied by alcohol smell. Degrees of alcohol: 12 °');
INSERT INTO boissons VALUES (Leffe Triple, 0.7, 4.2, 'leffetriple.jpg', 19, 24, 6, 'Leffe Triple is a true authentic abbey beer which, thanks to the presence of yeast, received a bottle fermentation. It offers a rich and nuanced flavor. Degrees of alcohol: 8.5 °');
INSERT INTO boissons VALUES (Chimay Blue, 0.7, 4.2, 'chimaybleue.jpg', 17, 48, 12, 'Chimay Blue is the strongest and most complex of the Trappist beers of the Abbey of Notre Dame de Scourmont. It offers a bouquet of spices and fruity nose, very characteristic of Chimay. Degrees of alcohol: 9 °');
INSERT INTO boissons VALUES (Cuvée des Trolls, 0.52, 3.1, 'trolls.jpg', 8, 24, 6, 'La Cuvée des Trolls is flavored with dried orange peel. They give this beer a note of freshness and particularly pleasant and fruity aromas. Degrees of alcohol: 7 °');
INSERT INTO boissons VALUES (Desperados, 0.65, 4.1, 'desperados.jpg', 34, 72, 24, 'The Desperados beer is a light blonde, flavored tequila. This beer is a special product that is at the boundary between light beer and alcoholic cocktail. Degrees of alcohol: 5.9 °');
INSERT INTO boissons VALUES (Gueuze Belle Vue, 0.51, 3, 'bellevue.jpg', 21, 24, 6, 'Geuze Bellevue is a spontaneous fermentation beer. It is composed of a mix of young and older lambic Lambic. Its taste is both a light, sweet, bitter and safe. Degrees of alcohol: 5.2 °');
INSERT INTO boissons VALUES (Tripel Karmeliet, 0.55, 3.6, 'karmeliet.jpg', 34, 48, 12, 'The Triple Karmeliet beer is one of the classic Belgian beers. On tasting, the Belgian beer has a golden blond color and a creamy foam. Degrees of alcohol: 8 °');
INSERT INTO boissons VALUES (Lindemans Peach, 0.65, 4.1, 'lindemans.jpg', 18, 24, 6, 'This is a Lindemans Lambic style beer, brewed with barley malt, wheat and peach juice before adding a period of maturation in oak barrels. Degrees of alcohol: 2.5 °');
INSERT INTO boissons VALUES (Rochefort 8°, 0.75, 4.6, 'rochefort8.jpg', 23, 48, 12, 'The Rochefort 8 is an original and flavored Trappist beer. First pungent then sweet. Tasting this beer is very pleased with the roundness given by the caramel and chocolate. Degrees of alcohol: 9.2 °');
INSERT INTO boissons VALUES (Rochefort 10°, 0.86, 5.6, 'rochefort10.jpg', 31, 48, 12, 'The Rochefort 10 ° is a brown Trappist beer. This is the most alcoholic beer of the Abbey of Rochefort and is also the richest. Degrees of alcohol: 11.3 °');
INSERT INTO boissons VALUES (Westmalle Triple, 0.71, 4.3, 'westmalle.jpg', 19, 48, 12, 'Open yourself to what is undoubtedly one of the most complex Trappist beers that exist with this Westmalle Triple. Degrees of alcohol: 9,5 °');
INSERT INTO boissons VALUES (Val Dieu Triple, 0.73, 4.5, 'valdieu.jpg', 41, 48, 12, 'The Val Dieu Triple is a Belgian high fermentation beer brewed in the Val-Dieu Abbey in Aubel following a recipe inspired by the one used by brewers monks. This blond beer with a slightly cloudy dress hides fine bubbles and has a beautiful creamy white foam. Degrees of alcohol: 9 °');
INSERT INTO boissons VALUES (Chouffe, 0.65, 4.1, 'chouffe.jpg', 35, 48, 12, 'La Chouffe is an unfiltered lager, bottled fermented three times. It is pleasantly fruity, spiced with coriander and slightly hoppy. Degrees of alcohol: 8 °');
INSERT INTO boissons VALUES (Orval, 0.77, 4.7, 'orval.jpg', 17, 48, 12, 'The Orval Trappist beer is a tasting at the beautiful orange color and has a lovely aromatic nose. It is a beer to intense hop aroma that reveals a slight acidity and astringent wins to mature in the cellar for a few years standing and protected from light. Degrees of alcohol: 6.2 °');
INSERT INTO boissons VALUES (Duvel, 0.63, 4, 'duvel.jpg', 33, 48, 12, 'Duvel beer is a fruity Belgian beer whose reputation is well established. The set appears extremely fruity anything faintly sweet and harmony of flavors actually mention the presence of alcohol in this lager. Degrees of alcohol: 8.5 °');
INSERT INTO boissons VALUES (Gauloise Blond, 0.51, 3, 'gauloiseblonde.jpg', 37, 48, 12, 'You will be quickly won over by its nice golden blond color and fine and creamy foam. In the mouth, the blonde Gauloise presents fruity flavors accompanied by spicy wedges. Degrees of alcohol: 6.3 °');
INSERT INTO boissons VALUES (Gauloise Amber, 0.51, 3, 'gauloiseambree.jpg', 32, 48, 12, 'The Gauloise Amber is characterized by hoppy aroma. It will give you a range of varied flavors. It is highly digestible and refreshing. Degrees of alcohol: 5.5 °');
INSERT INTO boissons VALUES (Gauloise Brown, 0.51, 3, 'gauloisebrune.jpg', 15, 24, 6, 'Its roasted malt gives it a caramelised taste with hints of caramel, chocolate and spices. It is characterized by a savory balance. Degrees of alcohol: 8.1 °');
INSERT INTO boissons VALUES (Pastis Ricard, 0.73, 4.5, 'pastis.jpg', 22, 40, 10, 'Ricard Pastis is an alcoholic beverage flavored with aniseed and liquorice. It is drunk as an aperitif, supplemented with water. Is generally added to five to seven volumes of fresh water for a volume of pastis.');
INSERT INTO boissons VALUES (Martini, 0.6, 3.9, 'martini.jpg', 26, 40, 10, 'Martini is an Italian brand of vermouth. It is eaten with ice, lemon or cocktail. Flavours available: Rosso or Bianco.');
INSERT INTO boissons VALUES (Martini Royal, 0.8, 5, 'martiniroyale.jpg', 13, 40, 10, 'Martini Royale is a wine-based aperitif, inspired by the famous cocktail, exclusive mix between the sweet bitterness of Martini Bianco and Rosso and the bright sparkle of MARTINI Prosecco.');
INSERT INTO boissons VALUES (Campari, 0.73, 4.5, 'campari.jpg', 28, 40, 10, 'Campari is an aperitif par excellence. Its typical bittersweet taste comes from plants, fruits and herbs, combined with sugar and distilled water.');
INSERT INTO boissons VALUES (Pisang, 0.73, 4.5, 'pisang.jpg', 35, 40, 10, 'Pisang is a green banana liqueur and tropical fruits, bright green, whose recipe is based on an Indonesian liquor.');
INSERT INTO boissons VALUES (Porto, 0.65, 4.1, 'porto.jpg', 18, 40, 10, 'Porto is certainly the most famous fortified wine. Like other fortified wines, it elaborates by adding alcohol to the must during fermentation.');
INSERT INTO boissons VALUES (Pineau des Charentes, 0.6, 3.9, 'pineau.jpg', 34, 40, 10, 'Pineau des Charentes is a mistelle, quality liqueur wine mash obtained by mixing and eau-de-vie brandy.');
INSERT INTO boissons VALUES (Picon, 0.72, 4.4, 'picon.jpg', 31, 40, 10, 'Picon is a bitter with a caramel color that traditionally accompanies the beer.');
INSERT INTO boissons VALUES (Kir Sauvignon, 0.71, 4.3, 'kir.jpg', 23, 40, 10, 'Kir is a traditional cocktail of Burgundy cuisine, blackcurrant cream base and Bourgogne Aligoté.');
INSERT INTO boissons VALUES (Kir Royal, 1.56, 9.7, 'kirroyal.jpg', 19, 40, 10, 'The Kir Royal is a variation of Sauvignon kir, based creme de cassis and champagne.');
INSERT INTO boissons VALUES (Mojito, 1.15, 7.5, 'mojito.jpg', 35, 50, 12, 'The Mojito is a rum cocktail, lime and mint leaves.');
INSERT INTO boissons VALUES (Cuba Libre, 1.15, 7.5, 'cubalibre.jpg', 41, 50, 12, 'Cuba Libre is a rum cocktail, lime and cola, also known as rum and Coke.');
INSERT INTO boissons VALUES (Bloody Mary, 1.15, 7.5, 'bloodymary.jpg', 44, 50, 12, 'Bloody Mary is a more or less strongly spiced cocktail, made with vodka, tomato juice, lemon juice and spices such as pepper, Tabasco sauce, Worcestershire sauce, pepper, celery salt ...');
INSERT INTO boissons VALUES (Margarita, 1.15, 7.5, 'margarita.jpg', 42, 50, 12, 'The Margarita is a cocktail made with tequila, cointreau and lemon juice.');
INSERT INTO boissons VALUES (Get 27, 0.77, 4.7, 'get27.jpg', 38, 50, 12, 'Get 27 is a liquor mint with essential oils of mint, alcohol, water and sugar. It is eaten with ice or diluted with water.');
INSERT INTO boissons VALUES (Grappa, 0.87, 5.7, 'grappa.jpg', 32, 50, 12, 'Grappa is a brandy life grape marc produced in Italy.');
INSERT INTO boissons VALUES (Vodka, 0.77, 4.7, 'vodka.jpg', 26, 40, 10, 'Vodka is a colorless liquor of 40 degrees. This is usually a potato water spirits or grain.');
INSERT INTO boissons VALUES (Gin, 0.77, 4.7, 'gin.jpg', 29, 40, 10, 'Gin is an alcoholic beverage obtained by flavoring rectified alcohol of agricultural origin with juniper berries.');
INSERT INTO boissons VALUES (Bacardi, 0.77, 4.7, 'bacardi.jpg', 25, 40, 10, 'Bacardi is a white rum produced from sugar cane.');
INSERT INTO boissons VALUES ("Bailey's", 0.77, 4.7, 'baileys.jpg', 28, 30, 5, 'Baileys Irish Cream is a liquor made from Irish whiskey and cream.');
INSERT INTO boissons VALUES (Tequila, 0.88, 5.8, 'tequila.jpg', 31, 40, 10, 'Tequila is an alcoholic drink produced in Mexico from a plant called Agave tequilana. Served with a quarter of lemon and salt.');
INSERT INTO boissons VALUES (William Lawson, 0.77, 4.7, 'williamlawson.jpg', 22, 40, 10, 'William Lawson is called a blend, that is to say a mixture of several kinds of whiskey.');
INSERT INTO boissons VALUES ("Jack Daniel's", 0.85, 5.5, 'jackdaniels.jpg', 18, 40, 10, 'The Jack Daniel's is a Tennessee Whiskey, it is mostly made from corn and meets all the criteria for designation of bourbon.');
INSERT INTO boissons VALUES (Amaretto, 0.77, 4.7, 'amaretto.jpg', 24, 40, 10, 'The amaretto is a liqueur with a light scent of bitter almonds, originally from Italy.');
INSERT INTO boissons VALUES (Cognac, 0.77, 4.7, 'cognac.jpg', 28, 40, 10, 'Cognac is a fine eau-de-vie made from grapes and aged for at least two and a half years in oak barrels.');
INSERT INTO boissons VALUES (Calvados, 1.05, 6.6, 'calvados.jpg', 33, 40, 10, 'Calvados is an eau-de-vie of Norman origin obtained by the distillation of cider or perry.');
INSERT INTO boissons VALUES (Cointreau, 1.05, 6.6, 'cointreau.jpg', 27, 40, 10, 'Cointreau is a liquor with a base of sweet and bitter orange peels.');
INSERT INTO boissons VALUES (Limoncello, 0.83, 5.3, 'limoncello.jpg', 35, 40, 10, 'Limoncello is a lemon liquor obtained by maceration of lemon peel in alcohol or grappa.');
